#include "tester.h"

void	gnl(int fd, char *expected)
{
	char	*actual = get_next_line(fd);
	ASSERT_EQ_STR(actual, expected);
	if (actual != NULL)
		free(actual);
}

int	main(void)
{
	setvbuf(stdout, (char *)NULL, _IONBF, 0);
	printf("<<<<<<<<<<<<<<<<     BUFFER_SIZE=%i     >>>>>>>>>>>>>>>>\n", BUFFER_SIZE);
	{
		printf("----------------%s----------------\n", "Invalid fd");
		gnl(1000, NULL);
		gnl(-1, NULL);
		gnl(3, NULL);
		printf("\n");
	}
	// TODO: if file is closed, fd is not changed and strange behaviour happnes.
	{
		char	*file_name = "files/empty";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, NULL);
		gnl(fd, NULL);
		printf("\n");
		close(fd);
	}
	{
		char	*file_name = "files/nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "\n");
		gnl(fd, NULL);
		printf("\n");
		close(fd);
	}
	{
		char	*file_name = "files/41_no_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "01234567890123456789012345678901234567890");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/41_with_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "0123456789012345678901234567890123456789\n");
		gnl(fd, "0");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/42_no_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "012345678901234567890123456789012345678901");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/42_with_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "01234567890123456789012345678901234567890\n");

		if (BUFFER_SIZE == 42)
		{
			// Test if gnl read exactly BUFFER_SIZE.
			char c;
			read(fd, &c, 1);
			ASSERT_EQ_I(c, '1');
		}
		else
			gnl(fd, "1");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/43_no_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "0123456789012345678901234567890123456789012");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/43_with_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "012345678901234567890123456789012345678901\n");
		gnl(fd, "2");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/multiple_nlx5";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "\n");
		gnl(fd, "\n");
		gnl(fd, "\n");
		gnl(fd, "\n");
		gnl(fd, "\n");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/multiple_line_no_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "01234567890123456789012345678901234567890\n");
		gnl(fd, "987654321098765432109876543210987654321098\n");
		gnl(fd, "0123456789012345678901234567890123456789012\n");
		gnl(fd, "987654321098765432109876543210987654321098\n");
		gnl(fd, "01234567890123456789012345678901234567890");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/multiple_line_with_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "9876543210987654321098765432109876543210\n");
		gnl(fd, "01234567890123456789012345678901234567890\n");
		gnl(fd, "987654321098765432109876543210987654321098\n");
		gnl(fd, "01234567890123456789012345678901234567890\n");
		gnl(fd, "9876543210987654321098765432109876543210\n");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/alternate_line_nl_with_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "01234567890123456789012345678901234567890\n");
		gnl(fd, "\n");
		gnl(fd, "987654321098765432109876543210987654321090\n");
		gnl(fd, "\n");
		gnl(fd, "0123456789012345678901234567890123456789012\n");
		gnl(fd, "\n");
		gnl(fd, "987654321098765432109876543210987654321090\n");
		gnl(fd, "\n");
		gnl(fd, "01234567890123456789012345678901234567890\n");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/big_line_no_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "files/big_line_with_nl";
		int		fd = open(file_name, O_RDONLY);
		printf("----------------%s----------------\n", file_name);
		gnl(fd, "\n");
		gnl(fd, NULL);
		close(fd);
		printf("\n");
	}
	{
		char	*file_name = "STDIN(./run-test <files/alternate_line_nl_with_nl)";
		printf("----------------%s----------------\n", file_name);
		gnl(STDIN_FILENO, "01234567890123456789012345678901234567890\n");
		gnl(STDIN_FILENO, "\n");
		gnl(STDIN_FILENO, "987654321098765432109876543210987654321090\n");
		gnl(STDIN_FILENO, "\n");
		gnl(STDIN_FILENO, "0123456789012345678901234567890123456789012\n");
		gnl(STDIN_FILENO, "\n");
		gnl(STDIN_FILENO, "987654321098765432109876543210987654321090\n");
		gnl(STDIN_FILENO, "\n");
		gnl(STDIN_FILENO, "01234567890123456789012345678901234567890\n");
		gnl(STDIN_FILENO, NULL);
		printf("\n");
	}
}
